var cursor=document.querySelector(".cursor"),cursorInnerCircle=document.querySelector(".inner-circle"),moveCursor=e=>{const r=e.clientY,s=e.clientX;cursor.style.transform=`translate3d(${s}px, ${r}px, 0)`};document.querySelectorAll("a").forEach((e=>{e.addEventListener("mouseleave",(()=>{cursor.classList.remove("hover-links"),cursorInnerCircle.classList.remove("hover-links")})),e.addEventListener("mouseover",(()=>{cursor.classList.add("hover-links"),cursorInnerCircle.classList.add("hover-links")}))})),window.addEventListener("mousemove",moveCursor);