var cursor=document.querySelector(".cursor"),moveCursor=e=>{const r=e.clientY,o=e.clientX;cursor.style.transform=`translate3d(${o}px, ${r}px, 0)`};document.querySelectorAll("a").forEach((e=>{e.addEventListener("mouseleave",(()=>{cursor.classList.remove("hover-links")})),e.addEventListener("mouseover",(()=>{cursor.classList.add("hover-links")}))})),window.addEventListener("mousemove",moveCursor);